package com.example.homework_2_final;import android.content.Context;import android.content.SharedPreferences;import android.preference.PreferenceManager;import com.google.gson.Gson;import com.google.gson.reflect.TypeToken;import java.lang.reflect.Type;import java.util.ArrayList;import java.util.List;public class Data_Persistence_Helper {    public static Context Context;    public static void StoreData(List<Car> cars)    {        SharedPreferences sp = PreferenceManager.getDefaultSharedPreferences(Context);        SharedPreferences.Editor editor = sp.edit();        String json = new Gson().toJson(cars);        editor.putString("cars", json);        editor.commit();    }    public static List<Car> LoadData() {        SharedPreferences sp = PreferenceManager.getDefaultSharedPreferences(Context);        String json = sp.getString("cars", null);        if (json != null) {            Type type = new TypeToken<List<Car>>() {            }.getType();            return new Gson().fromJson(json, type);        } else {            List<Car> cars = new ArrayList<>();            cars.add(new Car(R.drawable.aston_martin_db5_1963, "Aston Martin", "DB5","1963","$650,000"));            cars.add(new Car(R.drawable.aston_martin_db4_1961, "Aston Martin", "DB4","1961","$1,250,000"));            cars.add(new Car(R.drawable.ferrari_250_gt_swb_berlinetta_1961, "Ferrari", "250 GT SWB Berlinetta","1961","$7,320,000"));            cars.add(new Car(R.drawable.ferraris_dino_246, "Ferrari", "Dino 246","1969","$405,400"));            cars.add(new Car(R.drawable.lamborghini_espada_1977, "Lamborghini", "Espada","1977","$184,000"));            cars.add(new Car(R.drawable.lamborghini_miura_1968, "Lamborghini", "Miura","1968","$978,251"));            cars.add(new Car(R.drawable.leitner_porsche_911_turbo_10c_1993, "Porsche", "911 Turbo 10c","1993","$325,999"));            cars.add(new Car(R.drawable.porsche_911_turbo_3_0_1976, "Porsche", "911 Turbo 3.0","1976","$275,000"));            return cars;        }    }}